mapper.insert:
INSERT INTO stock_day_data (id, symbol, trade, price_change, change_percent, open, high, low, volume, amount, inside_dish, outer_disc, turnover_ratio, tick_time, update_time) VALUES
(#{item.id}, #{item.symbol}, #{item.trade}, #{item.priceChange}, #{item.changePercent}, #{item.open}, #{item.high}, #{item.low}, #{item.volume}, #{item.amount},#{item.insideDish},#{item.outerDisc}, #{item.turnoverRatio},#{item.tickTime}, NOW())
ON DUPLICATE KEY UPDATE symbol = VALUES (symbol), trade = VALUES (trade), price_change = VALUES (price_change), change_percent = VALUES (change_percent), open = VALUES (open), high = VALUES (high), low = VALUES (low), volume = VALUES (volume), amount = VALUES (amount), inside_dish = VALUES (inside_dish), outer_disc = VALUES (outer_disc), tick_time = VALUES (tick_time), turnover_ratio = VALUES (turnover_ratio), update_time = NOW();

mapper.select:
SELECT COUNT(1) > 0
FROM stock_day_data
WHERE tick_time = #{today}
